import { mockServerPool } from "../mock-server/MockServerPool";
import { ElevenLabsClient } from "../../src/Client";

describe("Usage", () => {
    test("get", async () => {
        const server = mockServerPool.createServer();
        const client = new ElevenLabsClient({
            apiKey: "test",
            environment: { base: server.baseUrl, wss: server.baseUrl },
        });

        const rawResponseBody = { time: [1738252091000, 1739404800000], usage: { All: [49, 1053] } };
        server
            .mockEndpoint()
            .get("/v1/usage/character-stats")
            .respondWith()
            .statusCode(200)
            .jsonBody(rawResponseBody)
            .build();

        const response = await client.usage.get({
            startUnix: 1,
            endUnix: 1,
        });
        expect(response).toEqual({
            time: [1738252091000, 1739404800000],
            usage: {
                All: [49, 1053],
            },
        });
    });
});
